mapper.update:
UPDATE CHAT_ROOM
SET ROOM_DELETED_FL = 'Y'
WHERE ROOM_NO = #{roomNo}

mapper.select:
SELECT OWNER_MEMBER_NO
FROM CHAT_ROOM
WHERE ROOM_NO = #{roomNo}

mapper.insert:
INSERT INTO CHAT_ROOM_USER (
  ROOM_NO, MEMBER_NO
) VALUES (
  #{roomNo}, #{memberNo}
)

mapper.delete:
DELETE FROM CHAT_ROOM_USER
WHERE ROOM_NO = #{roomNo}
AND MEMBER_NO = #{memberNo}
