mapper.select:
SELECT COUNT(*) FROM BOARD
WHERE BOARD_CD = ${boardCode}
AND BOARD_DEL_FL = 'N'

mapper.insert:
INSERT INTO IMG SELECT SEQ_IMG_NO.NEXTVAL, A.* FROM (
SELECT #{img.imageReName} IMG_RENAME, #{img.imagePath} IMG_PATH, #{img.imageOriginal} IMG_ORIGINAL, #{img.imageLevel} IMG_LEVEL, #{img.imageType} IMG_TYPE, #{img.imageTypeNo} IMG_TYPE_NO FROM DUAL
)A
