mapper.insert:
INSERT INTO IMG (IMG_NO, IMG_PATH, IMG_RENAME, IMG_ORIGINAL, IMG_LEVEL, IMG_TYPE, IMG_TYPE_NO) SELECT SEQ_IMG_NO.NEXTVAL, A.IMG_PATH, A.IMG_RENAME, A.IMG_ORIGINAL, A.IMG_LEVEL, A.IMG_TYPE, A.IMG_TYPE_NO FROM (
SELECT #{img.imagePath} AS IMG_PATH, #{img.imageReName} AS IMG_RENAME, #{img.imageOriginal} AS IMG_ORIGINAL, #{img.imageLevel} AS IMG_LEVEL, #{img.imageType} AS IMG_TYPE, #{img.imageTypeNo} AS IMG_TYPE_NO FROM DUAL
) A
